sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

isDatePast: function(whenDate){
//get current datetime
var now = new GlideDateTime();

//instantiate the u_when_needed field
var whenNeeded = new GlideDateTime(whenDate);

//abort if whenNeeded < now
if(whenNeeded.before(now)){
	return true;
}
else{
	return false;
}
},

isSameDay: function(whenDate){
var now = new GlideDateTime();
var whenNeeded = new GlideDateTime(whenDate);
var dateNow = now.getDate();
var datewhenNeeded = whenNeeded.getDate();

if(dateNow == datewhenNeeded){
	return true;
}
else {
	return false;
}
},
    type: 'NeedItUtils'
};
